sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    var mail = request.pathParams.email;
    var message = 'User not Available with that email';
    var userName = '';
    var statusCode = 400;
    var bodyObj = {};
    var userGR = new GlideRecord("sys_user");
    userGR.addEncodedQuery("email=" + mail);
    userGR.query();
    if (userGR.next()) {
        userName = userGR.user_name.toString();
		var firstname = userGR.first_name.toString();
		var secondname = userGR.last_name.toString();
        var vip = userGR.vip.toString();
        statusCode = 200;
        // bodyObj["User Name"] = userName;
    }
    if (statusCode == 200) {
        bodyObj["User Name"] = userName;
		bodyObj["First Name"] = firstname;
		bodyObj["Second Name"] = secondname;
		bodyObj["VIP"] = vip;
    } else {
		bodyObj["Message"] = message;
    }
	response.setBody(bodyObj);
    response.setStatus(statusCode);

    // select * from sys_user where sys_id = "fa5fd74d83f122100daa76d6feaad398";

})(request, response);
