sys_ws_operation.operation_script:
(function process(request, response) {

    var body = request.body.data;

    gs.info('Raw req body.body =' + body);

    // Parse if request.body is a string
    if (typeof body === "string") {
        try {
            body = JSON.parse(body);
        } catch (e) {
            response.setBody({
                result: {
                    error: "Invalid JSON in request body"
                }
            });
            return;
        }
    }

    // Validate payload
    if (!body || !body.data || !Array.isArray(body.data)) {
        response.setBody({
            result: {
                error: "Request must contain 'data' array with city and ipaddress"
            }
        });
        return;
    }

    var resultArr = [];
    var API_KEY = "AIzaSyB7AKyZsb-AXzCA7TBPgu6PoRI1oaAniS4"; // ðŸ”‘ Google API key

    body.data.forEach(function(item) {
        var city = item.city || "";
        var ip = item.ipaddress || "";

        var lat = 0.0;
        var lng = 0.0;

        try {
            if (city) {


                // Call Google Geocoding API
                var r = new sn_ws.RESTMessageV2();
                r.setHttpMethod("POST");
                r.setEndpoint("https://maps.googleapis.com/maps/api/geocode/json?address=" + encodeURIComponent(city) + "&key=" + API_KEY);

                var res = r.execute();
                var resBody = JSON.parse(res.getBody());

                if (resBody.results && resBody.results.length > 0) {
                    lat = resBody.results[0].geometry.location.lat;
                    lng = resBody.results[0].geometry.location.lng;
                }

            }
        } catch (ex) {
            // Capture any error but still return city + ip
            gs.error("Error fetching location for " + city + ": " + ex.message);
        }

        resultArr.push({
            city: city,
            ipaddress: ip,
            latitude: lat,
            longitude: lng
        });
    });

    response.setBody({
        result: resultArr
    });

})(request, response);
